      accounts = await web3.eth.getAccounts();//.send({from:accounts[0]})
      if(accounts[0] !== ""){
        //accounts[0
        //document.getElementById("bu").
        //document.getElementById("bu").append("CONNECTED")
        btn= document.getElementById("bu");
        //btn.value = accounts[0]; // will just add a hidden value
        //btn.innerHTML = accounts[0];
        btn.innerHTML = "CONNECTED"
        localStorage.setItem('myaddress', accounts[0]);    
  }
      else{
        //document.getElementById("bu").remove("");
        //document.getElementById("bu").replaceWith("NOT CONNECTED")
        var btns = document.getElementById("bu");
        //btns.value = accounts[0]; // will just add a hidden value
        btns.innerHTML = "NOT CONNECTED";
        localStorage.setItem('myaddress', "");
      }
  };    
  useEffect(()=>{connectmm()},[]);
  return (
    <div>

      <div class="display-4 mb-1"></div>
      <div style={{backgroundColor:'white',height:'70px',width:'1500px',marginBlock:'5px',display:'flex'}}>
      <div style={{backgroundColor:'white',height:'43px',width:'1050px',marginLeft:'150px',marginBlock:'15px'}}>
      <Link
              to="/">
              <button
              style={{outline: 'none'}}
                class="btn btn-info btn-block"
                type="button"
              >
                Home
              </button>
      </Link>
      {" "}
      <Link
              to="/explore">        
              <button
              style={{outline: 'none'}}
                class="btn btn-info btn-block"
                type="button"
              >
                Explore
              </button>
      </Link>
      {" "}
      <Link
              to="/salepagecopy">
              <button
                class="btn btn-info btn-block"
                type="button"
                // onClick={() => {
                //   history.push("/Salepagecopy");
                // }}
              >
                Myitem     
              </button>
              </Link>
              {" "}
              <Link
              to="/followingpage">
              <button
                class="btn btn-info btn-block"
                type="button"
                // onClick={() => {
                //   history.push("/Followingpage");
                // }}
              >
               Following
              </button>
              </Link>
              {" "}
              <Link
              to="/activitypage">
              <button
                class="btn btn-info btn-block"
                type="button"
                // onClick={() => {
                //   history.push("/Activitypage");
                // }}
              >
               Activity
              </button>
              </Link>
              {" "}
              <Link
              to="/howitworkpage">
              <button
                class="btn btn-info btn-block"
                type="button"                
              >
               How it works
              </button>
</Link>
              {" "}
              <Link
              to="/communitypage">
              <button
                class="btn btn-info btn-block"
                type="button"                
              >
               Community
              </button>
              </Link>
              {" "}
              <Link
              to="/nft">
              <button
                class="btn btn-info btn-block"
                type="button"
                // onClick={() => {
                //   history.push("/Nft");
                // }}
              >
               Create
              </button>
              </Link>
              {" "}
              <button
              id="bu"
                class="btn btn-info btn-block"
                type="button"
                onClick= {connectmm}>
               Connect wallet
              </button>
              {" "}
</div>
</div>
              <br></br>
<br></br>
<br></br>
<br></br>
<div>
<div>
    <Online>
</Online>
    </div>
    <div>
    {/* <Offline>
    {<Popup content={<>
        <b>Notification</b>
        <p>Your are offline please check your internet connection......</p>
        <center>
        {/* <button type="button" onClick={togglePopup}>close</button> */}
        {/* </center> */}
      {/* </>} */}
    {/* //  handleClose={togglePopup} */}
    {/* />} */}
    {/* </Offline> */}
    </div>
</div>              
            {/* <Router history={history}>
          <Switch>
            <Route path="/" exact>
              <div class="display-4 mb-1">Choose a route to go to</div>              
            </Route>
            <Route path="/Myitem">
              <Myitem />
            </Route>
            <Route path="/Activitypage">
              <Activitypage />
            </Route>
            <Route path="/Howitworkpage">
              <Howitworkpage />
            </Route>
            <Route path="/Followingpage">
              <Followingpage />
            </Route>
            <Route path="/Nft">
              <Nft />
            </Route>
            <Route path="/Mypurchasepage">
              <Mypurchasepage />
            </Route>
            <Route path="/Explore">
              <Explore />
            </Route>
            <Route path="/Createandpurchasepage">
              <Createandpurchasepage />
            </Route>
            <Route path="/Receivedpage">
              <Receivedpage />
            </Route>
          </Switch>
        </Router>
 */}
<div style={{backgroundColor:'white',height:'150px',width:'1500px',marginBlock:'5px',display:'flex',marginLeft:'2px',position:'relative'}} >
  <Footer/>
  </div> 
    </div>

  );
}
export default Communitypage;
