//    console.log("mani",address)

//    setGetAddressDb(address)


//  }

 




 const getAdressbyAccount = async  ()=>{
  const accounts = await web3.eth.getAccounts();


    // console.log("getaddress0",accounts[0])
  
    // console.log("function called");
   let address =[];
   let add=fireDb.database().ref(`contractaddress/${accounts[0]}`);
   console.log("addget",add)
   fireDb.database().ref(`contractaddress/${accounts[0]}`).on("value",(snapshot) =>{
     snapshot.forEach((s)=>{
       address.push(s.val());
     });
     
   });

   //console.log("mani",address)

   setGetAddressDb(address)

   
 }

  
  useEffect(()=>{
getAdressbyAccount()
  },[afternames])



 const newget = async()=>{
  const accounts = await web3.eth.getAccounts();
  let n =[]
  getAddressDb.forEach(async(add)=>{
    let poda = add
        let getaaaa=new web3.eth.Contract(abi,poda);
        
        
        let printgeta=await getaaaa.methods.tokensOfOwner(accounts[0]).call();

        printgeta.forEach((p=>{
n.push(p)
}))

      setnames(n);    
      
    })
    
    
  


 }

 const s =  async ()=>{
  const accounts = await web3.eth.getAccounts();

  let after = []
   getAddressDb.forEach(async(a)=>{
       let poda = a
        let getaaaa=new web3.eth.Contract(abi,poda);
        names.forEach(async (n)=>{
          console.log(`n`, n)
          // console.log(`object`,n, await getaaaa.methods.tokenURI(n).call())     }) 
          after.push({
            add:poda,
            addImgSrc:await getaaaa.methods.tokenURI(n).call()})
   })
  })

   console.log(`after`, after)
setAfternames(after)
 }


   
const setprice =async (a)=>{
  console.log(`a`, a)
  let getaaaa=new web3.eth.Contract(abi,a.add);
            const accounts = await  web3.eth.getAccounts();
        await getaaaa.methods.setTokenState(a.addImgSrc,"true").send({from:accounts[0]});
           // salepage.settokenstate();
            console.log("checking")
            var price = window.prompt("enter the price for your token");
            await getaaaa.methods.setTokenPrice(a.addImgSrc,price).send({from:accounts[0]})
          const priceamount = await getaaaa.methods.items(a.addImgSrc).call();
            console.log(priceamount.price)
           
            


}


  

  return (    

    <div className="App">


<button
                class="btn btn-info btn-block"
                type="button"
                onClick={() => {
                  history.push("/Allcontractpage");
                }}>
                Explore
              </button>

     
      
<button
                class="btn btn-info btn-block"
                type="button"
                onClick={() => {
                  history.push("/Salepagecopy");
                }}>
                My items
              </button>
              
              <button
                class="btn btn-info btn-block"
                type="button"
                onClick={() => {
                  history.push("/Followingpage");
                }}
              >
                Following
              </button>

              <button
                class="btn btn-info btn-block"
                type="button"
                onClick={() => {
                  history.push("/Activitypage");
                }}>
                Activity
              </button>
              <button
                class="btn btn-info btn-block"
                type="button"
                onClick={() => {
                  history.push("/Howitworkpage");
                }}
              >
                How it work
              </button>

              <button
                class="btn btn-info btn-block"
                type="button"
                onClick={() => {
                  history.push("/Communitypage");
                }}
              >
                Community
              </button>
              <button
                class="btn btn-info btn-block"
                type="button"
                onClick={() => {
                     history.push("/Nft");
                }}
              >
              Create
              </button>





              <br></br>
<br></br>










<button
                class="btn btn-info btn-block"
                type="button"
               onClick={s}
              >
              
              </button>

              <button
                class="btn btn-info btn-block"
                type="button"
                onClick={newget}
              >
              
              </button>
{afternames.length === 0 ? null : 
<div style={{width:'400px',height:'70vh',backgroundColor:'red',display:'flex',flexDirection:'column',flexWrap:'wrap'}}>
{afternames.map((a)=>{
  console.log(`a`, a)

  if(a.addImgSrc !=='')
  return (
    <div>

  <img   src={a.addImgSrc}  style={{height:100,width:200}}     />
  <button onClick={()=>setprice(a)} >SetPrice</button>
  
  </div>
  )

})}
</div>
}


      
<center>
<br></br>




	      
<br></br>
<br></br>


</center>

<br></br>
<br></br>



                    
                    

            <Router history={history}>
          <Switch>
            <Route path="/" exact>
              <div class="display-4 mb-1">Choose a route to go to</div>
              
            </Route>
            
            <Route path="/Myitem">
              <Myitem />
            </Route>
            <Route path="/nft">
              <Nft />
            </Route>
            <Route path="/sendpage">
              <Sendpage />
            </Route>
            <Route path="/tokencreate">
              <Tokencreate />
            </Route>
            <Route path="/newpage">
              <Newpage />
            </Route>
            <Route path="/printallimage">
              <Printallimage />
            </Route>
            <Route path="/Saleimagepage">
              <Saleimagepage />
            </Route>
          </Switch>
        </Router>




        
        <div>
            
        </div>


<div style={{flexDirection:'column',width:'500px',flexWrap:'wrap'}}>

<ul id="prabha">

</ul>


<ul id="prag">


</ul>

<ul id="ram" >
  
</ul>

    </div>
	  
      </div>      
